import { catchError, map, mergeMap, of, tap } from 'rxjs';

import { Injectable } from '@angular/core';
import { Router } from '@angular/router';

import { Actions, createEffect, ofType } from '@ngrx/effects';

import { ERoute } from '@core/enum/route.enum';
import { AuthService } from '@core/service/auth/auth.service';
import { LocalStorageService } from '@core/service/local-storage/local-storage.service';

import { TProfile } from '../model/profile.type';
import { AuthActions } from './auth.actions';

@Injectable()
export class AuthEffects {
    constructor(
        private _actions$: Actions,
        private _authService$: AuthService,
        private _localStorageService: LocalStorageService,
        private _router: Router,
    ) {}

    loginUser$ = createEffect(() =>
        this._actions$.pipe(
            ofType(AuthActions.getLogin),
            mergeMap((action) =>
                this._authService$.signIn(action.email, action.password).pipe(
                    map((userCredential) => {
                        const user: TProfile = {
                            uid: userCredential.user?.uid,
                            email: userCredential.user?.email,
                            displayName: userCredential.user?.displayName,
                            photoURL: userCredential.user?.photoURL,
                            emailVerified: userCredential.user?.emailVerified,
                        };
                        return AuthActions.getLoginSuccess({ user });
                    }),
                    catchError((error) =>
                        of(AuthActions.getLoginError({ error })),
                    ),
                ),
            ),
        ),
    );

    loginGuest$ = createEffect(() =>
        this._actions$.pipe(
            ofType(AuthActions.getLoginGuest),
            mergeMap(() =>
                this._authService$.signInAsGuest().pipe(
                    map(() => AuthActions.getLoginGuestSuccess()),
                    catchError((error) =>
                        of(AuthActions.getLoginGuestError({ error })),
                    ),
                ),
            ),
        ),
    );

    logoutUser$ = createEffect(() =>
        this._actions$.pipe(
            ofType(AuthActions.getLogout),
            mergeMap(() =>
                this._authService$.signOut().pipe(
                    map(() => AuthActions.getLogoutSuccess()),
                    tap(() => this._router.navigate([ERoute.AUTH])),
                    tap(() => this._localStorageService.clearUserData()),
                    catchError((error) =>
                        of(AuthActions.getLogoutError({ error })),
                    ),
                ),
            ),
        ),
    );
}

import { Observable } from 'rxjs';

import { Injectable } from '@angular/core';
import { Router, UrlTree } from '@angular/router';

import { ERoute } from '@core/enum/route.enum';
import { AuthService } from '@core/service/auth/auth.service';

@Injectable({
    providedIn: 'root',
})
export class AuthGuard {
    constructor(
        private readonly _authService: AuthService,
        private _router: Router,
    ) {}

    canActivate(): Observable<boolean> | Promise<boolean> | UrlTree | boolean {
        console.log('AuthGuard canActivate:', this._authService.isAuth$.value);
        if (!this._authService.isAuth$.value) {
            this._router.navigate([ERoute.AUTH]);
            return false;
        }
        return true;
    }
}

import { User } from 'firebase/auth';
import {
    BehaviorSubject,
    Subject,
    catchError,
    from,
    takeUntil,
    tap,
} from 'rxjs';

import { isPlatformBrowser } from '@angular/common';
import { Inject, Injectable, OnDestroy, PLATFORM_ID } from '@angular/core';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import {
    AngularFirestore,
    AngularFirestoreDocument,
} from '@angular/fire/compat/firestore';
import { Router } from '@angular/router';

import { Store } from '@ngrx/store';

import { ERoute } from '@core/enum/route.enum';

import { AuthActions } from '@layout/store/auth-store/auth.actions';
import { TProfile } from '@layout/store/model/profile.type';

import { LocalStorageService } from '../local-storage/local-storage.service';

@Injectable({
    providedIn: 'root',
})
export class AuthService implements OnDestroy {
    public userData: User | null = null;
    public isAuth$: BehaviorSubject<boolean> = new BehaviorSubject(false);
    public usersState: any;

    private _destroyed$: Subject<void> = new Subject();
    private _isBrowser: boolean;

    constructor(
        private readonly _afs: AngularFirestore,
        private readonly _afAuth: AngularFireAuth,
        private readonly _router: Router,
        private readonly _localStorageService: LocalStorageService,
        private readonly _store$: Store,
        @Inject(PLATFORM_ID) private platformId: string,
    ) {
        this._isBrowser = isPlatformBrowser(this.platformId);
        if (this._isBrowser) {
            this.usersState = this._localStorageService.getUsersState();
            if (localStorage.getItem('usersState')) {
                this.isAuth$.next(true);
            } else {
                this.isAuth$.next(false);
            }
        } else {
            this.isAuth$.next(false);
        }
    }

    signIn(email: string, password: string) {
        return from(
            this._afAuth.signInWithEmailAndPassword(email, password),
        ).pipe(
            tap((result) => {
                if (this._isBrowser) {
                    if (!this.usersState) {
                        this._localStorageService.initUser(
                            true,
                            false,
                            result.user,
                            'main',
                        );
                    } else {
                        console.log('Setting user in signIn:', result.user); //    
                        this._localStorageService.setUser(result.user);
                    }

                    this.setUserData(result.user);
                    if (result.user) {
                        this.isAuth$.next(true);
                        this._afAuth.authState
                            .pipe(takeUntil(this._destroyed$))
                            .subscribe((user) => {
                                if (user && this.isAuth$.value) {
                                    this._router.navigate([ERoute.LAYOUT]);
                                }
                            });
                    }
                }
            }),
            catchError((error: Error) => {
                this.isAuth$.next(false);
                throw error;
            }),
        );
    }

    signInAsGuest() {
        return from(this._afAuth.signInAnonymously()).pipe(
            tap((result) => {
                if (this._isBrowser) {
                    console.log('signInAsGuest result:', result);
                    if (!this.usersState) {
                        this._localStorageService.initUser(
                            true,
                            true,
                            null,
                            'main',
                        );
                    } else {
                        this._localStorageService.setUser(null);
                    }
                    this.isAuth$.next(true);
                    console.log(
                        'isAuth$ after signInAsGuest:',
                        this.isAuth$.value,
                    );
                    this._router.navigate([ERoute.LAYOUT]);
                }
            }),
            catchError((error: Error) => {
                this.isAuth$.next(false);
                console.error('signInAsGuest error:', error);
                throw error;
            }),
        );
    }

    setUserData(user: firebase.default.User | null) {
        const userRef: AngularFirestoreDocument<TProfile> = this._afs.doc(
            `users/${user?.uid}`,
        );
        const userData: TProfile = {
            uid: user?.uid,
            email: user?.email,
            displayName: user?.displayName,
            photoURL: user?.photoURL,
            emailVerified: user?.emailVerified,
        };
        return from(userRef.set(userData, { merge: true }));
    }

    signOut() {
        return from(this._afAuth.signOut()).pipe(
            tap(() => {
                if (this._isBrowser) {
                    const usersState =
                        this._localStorageService.getUsersState();

                    if (usersState?.isGuest) {
                        usersState.isGuest = false;
                    }
                    if (usersState?.user) {
                        usersState.user = null;
                    }

                    if (usersState) {
                        this._localStorageService.setUsersState(usersState);
                    }

                    this._localStorageService.clearUserData();
                    this._store$.dispatch(AuthActions.getLogout());
                    this.isAuth$.next(false);
                    this._router.navigate([ERoute.AUTH]);
                }
            }),
        );
    }

    ngOnDestroy(): void {
        this._destroyed$.next();
        this._destroyed$.complete();
    }
}

