@use '../../../../assets/styles/variables' as color;

.short-button {
    width: 280px;
}

.long-button {
    width: 300px;
}

.general-button {
    display: flex;
    position: relative;
    justify-content: center;
    transition: max-width 0.7s ease-in-out;
    cursor: pointer;
    border-radius: 50px;
    background-color: color.$black;
    padding: calc(13px + 10 * (100vw / 1920)) calc(15px + 50 * (100vw / 1920));
    overflow: hidden;
    color: color.$white;
    font-size: calc(4px + 12 * (100vw / 1920));
    white-space: nowrap;

    .general-button_text {
        transform: translateY(0);
        visibility: visible;
        z-index: 1;
        transition:
            transform 0.6s ease-in-out,
            opacity 0.6s ease-in-out,
            z-index 0s 0.6s,
            display 0.6s ease-in-out;

        &.generalButtonActive {
            transform: translateY(-100%);
            opacity: 0;
            transition:
                transform 0.6s ease-in-out,
                opacity 0.6s ease-in-out;
        }
    }

    .general-button_text-absolute {
        display: flex;
        position: absolute;
        top: 100%;
        left: 50%;
        justify-content: center;
        align-items: center;
        transform: translate(-50%, 0);
        opacity: 0;
        z-index: 2;
        transition:
            transform 0.6s ease-in-out,
            opacity 0.6s ease-in-out,
            z-index 0s 0.6s;
        border-radius: 50%;
        background-color: color.$blue;
        line-height: 86px;
        text-align: center;

        .hovered-text {
            transition: opacity 0.6s ease-in-out;
        }
    }

    &:hover .general-button_text-absolute {
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        opacity: 1;
        color: color.$white;
    }

    &:hover .general-button_text-absolute .hovered-text {
        opacity: 1;
    }

    &:not(:hover) .general-button_text-absolute {
        top: 100%;
        transform: translate(-50%, 0);
        opacity: 0;
    }

    &:not(:hover) .general-button_text-absolute .hovered-text {
        opacity: 0;
    }
}

.general-button:hover .general-button_text {
    z-index: 2;
}

.general-button:hover .general-button_text-absolute {
    z-index: 1;
}

.dark-theme {
    transition: 0.3s;
    background-color: color.$white;
    color: color.$black;
}

.connect-me {
    width: 100%;
    height: 280px;
}

.watch-cv {
    width: 100%;
    height: 300px;
}

.connect-me,
.watch-cv {
    display: flex;
    justify-content: center;
    align-items: center;
}

