        .card-img {
            border-radius: 28px;
            max-width: 15rem;
        }
    }

    .card-info {
        display: flex;
        flex-direction: column;
        height: 100%;

        &_place {
            margin-bottom: 8px;
            color: color.$darkGray;
        }

        &_place-name {
            margin-bottom: 20px;
            font-weight: bold;
            font-size: 20px;
        }

        &_time {
            font-size: 16px;
        }
    }

    .card-button__container {
        display: flex;
        position: relative;
        justify-content: center;
        align-items: center;
        transition: transform 0.5s ease;
        border-radius: 50%;
        padding: 2px;

        .card-button {
            z-index: 1;
            border-radius: 50%;
            background: color.$white;
            padding: 23px;
        }

        &::before {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            z-index: 0;
            border: 2px solid transparent;
            border-radius: 50%;
            width: 100%;
            height: 100%;
            content: '';
        }
    }

    &:hover .card-button__container::before {
        animation: rotateCircle 1s linear infinite;
    }
}

.experience-card.activeWork {
    justify-content: flex-end;

    .card-info {
        align-items: flex-end;
    }
}

.experience-card.activeEducation {
    justify-content: flex-start;

    .card-info {
        align-items: flex-start;
    }
}

.experience-card.activeWork:hover .card-button__container {
    transform: rotate(-90deg);
}

@keyframes rotateCircle {
    0% {
        transform: translate(-50%, -50%) rotate(0deg) translate(50px);
    }
    100% {
        transform: translate(-50%, -50%) rotate(360deg) translate(50px);
    }
}

@use '../../../../../../assets/styles/variables' as color;
@use '../../../../../../assets/styles/mixins/glassmorphism.mixin' as glassmorphism;

.experience-card.dark-theme {
    @include glassmorphism.cards_glassmorphism-dm;

    .experience-card_picture-work,
    .experience-card_picture-education {
        background-color: color.$dm-black;
    }

    .card-info {
        &_place {
            color: color.$dm-dark-text-title;
        }

        &_place-name {
            color: color.$white;
        }

        &_time {
            color: color.$dm-dark-text;
        }
    }

    .card-button {
        background: color.$black;
    }
}

