import { Observable } from 'rxjs';

import { AsyncPipe, NgSwitch } from '@angular/common';
import {
    ChangeDetectionStrategy,
    ChangeDetectorRef,
    Component,
    OnInit,
} from '@angular/core';

import { Store, select } from '@ngrx/store';

import { TExperienceAside } from '@core/models/experience-aside.type';
import { IExperience } from '@core/models/experience.interface';

import { darkModeSelector } from '@layout/store/dark-mode-store/dark-mode.selectors';
import { FirebaseActions } from '@layout/store/firebase-store/firebase.actions';
import {
    selectEducation,
    selectExperienceAside,
    selectWorkExperience,
} from '@layout/store/firebase-store/firebase.selectors';
import { TDarkMode } from '@layout/store/model/dark-mode.type';

import { AsideNavigationExperienceComponent } from '../../components/aside-navigation-experience/aside-navigation-experience.component';
import { EducationExperienceComponent } from './education-experience/education-experience.component';
import { WorkExperienceComponent } from './work-experience/work-experience.component';

@Component({
    selector: 'cv-experience',
    standalone: true,
    imports: [
        EducationExperienceComponent,
        WorkExperienceComponent,
        AsideNavigationExperienceComponent,
        AsyncPipe,
    ],
    templateUrl: './experience.component.html',
    styleUrl: './experience.component.scss',
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ExperienceComponent implements OnInit {
    public experienceAside$: Observable<TExperienceAside[]> = this._store$.pipe(
        select(selectExperienceAside),
    );

    public currentTheme$: Observable<boolean> = this._store$.pipe(
        select(darkModeSelector),
    );

    public workPlace$: Observable<IExperience[]> = this._store$.pipe(
        select(selectWorkExperience),
    );
    public selectedTab: string = '';

    public educationPlace$: Observable<IExperience[]> = this._store$.pipe(
        select(selectEducation),
    );

    public switchTab($event: string) {
        this.selectedTab = $event;
        this._cd.detectChanges();
    }

    constructor(
        private _store$: Store<
            TDarkMode | IExperience | IExperience | TExperienceAside
        >,
        private _cd: ChangeDetectorRef,
    ) {}

    ngOnInit(): void {
        this.switchTab('work');
        this._store$.dispatch(
            FirebaseActions.getExperienceAside({ imgName: '' }),
        );
        this._store$.dispatch(
            FirebaseActions.getWorkExperience({ imgName: 'companies-logo' }),
        );
        this._store$.dispatch(
            FirebaseActions.getEducationPlaces({ imgName: 'certificates' }),
        );
    }
}

import { Observable } from 'rxjs';

import { AsyncPipe, NgIf } from '@angular/common';
import {
    ChangeDetectionStrategy,
    Component,
    ElementRef,
    HostListener,
    Inject,
    OnInit,
    ViewChild,
    input,
} from '@angular/core';
import { FormGroup, ReactiveFormsModule } from '@angular/forms';

import { Store, select } from '@ngrx/store';

import { IExperience } from '@core/models/experience.interface';

import { ExperienceActions } from '@layout/store/experience-dialog-store/experience-dialog.actions';
import { selectModalData } from '@layout/store/experience-dialog-store/experience-dialog.selectors';
import { TProfile } from '@layout/store/model/profile.type';

@Component({
    selector: 'cv-experience-dialog',
    standalone: true,
    imports: [ReactiveFormsModule, AsyncPipe, NgIf],
    templateUrl: './experience-dialog.component.html',
    styleUrl: './experience-dialog.component.scss',
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ExperienceDialogComponent implements OnInit {
    @ViewChild('modal', { static: false })
    public modal!: ElementRef;
    @HostListener('document:mousemove', ['$event'])
    public modalData$!: Observable<IExperience | null>;
    public header = input.required<string>();
    public authForm!: FormGroup;
    public user: TProfile | null = null;

    constructor(
        @Inject(Store)
        private _store$: Store<IExperience | IExperience>,
    ) {}

    ngOnInit(): void {
        this.modalData$ = this._store$.pipe(select(selectModalData));
    }

    public onMouseMove(event: MouseEvent) {
        const target = event.target as HTMLElement;
        if (!this.modal.nativeElement.contains(target)) {
            this.modal.nativeElement.classList.add('dimmed');
        } else {
            this.modal.nativeElement.classList.remove('dimmed');
        }
    }

    public onBackgroundClick(event: MouseEvent): void {
        const target = event.target as HTMLElement;
        if (target.classList.contains(this.modal.nativeElement.classList)) {
            this._store$.dispatch(
                ExperienceActions.getExperienceDialogClosed(),
            );
        }
    }

    public closeModalDialog() {
        this._store$.dispatch(ExperienceActions.getExperienceDialogClosed());
    }
}

