    public isExperienceDialogVisible$!: Observable<boolean>;
    public modalData$!: Observable<IExperience | null>;

    private _destroyed$: Subject<void> = new Subject();

    public currentTheme$: Observable<boolean> = this._store$.pipe(
        takeUntil(this._destroyed$),
        select(darkModeSelector),
    );

    public getModalInstance($event: boolean) {
        this.isModalDialogVisible = $event;
        console.log(this.isModalDialogVisible);
    }

    public navigation$: Observable<INavigation[]> = this._store$.pipe(
        takeUntil(this._destroyed$),
        select(selectNavigation),
    );

    public social$: Observable<ISocialMedia[]> = this._store$.pipe(
        takeUntil(this._destroyed$),
        select(selectSocialMediaLinks),
    );

    constructor(
        @Inject(Store)
        private _store$: Store<
            TDarkMode | INavigation | ISocialMedia | { modal: ModalState }
        >,
        @Inject(AngularFireAuth) public afAuth: AngularFireAuth,
        private _localStorageService: LocalStorageService,
        private _cdr: ChangeDetectorRef,
    ) {
        this.isFirstTime = this._localStorageService.getIsFirstTime();
    }

    ngOnInit(): void {
        this._store$.dispatch(FirebaseActions.getNavigation({ imgName: '' }));
        this._store$.dispatch(FirebaseActions.getSocialMedia({ imgName: '' }));

        this.afAuth.authState
            .pipe(takeUntil(this._destroyed$))
            .subscribe((user) => {
                if (!user) {
                    this.isModalDialogVisible = true;
                }
            });

        this.isExperienceDialogVisible$ = this._store$.pipe(
            takeUntil(this._destroyed$),
            select(selectIsModalOpen),
        );
        this.modalData$ = this._store$.pipe(
            takeUntil(this._destroyed$),
            select(selectModalData),
        );

        if (this.isFirstTime) {
            timer(12000)
                .pipe(takeUntil(this._destroyed$))
                .subscribe(() => {
                    this.isFirstTime = false;
                    this._localStorageService.setIsFirstTime(false);
                    this._cdr.markForCheck();
                });
        }
    }

    public prepareRoute(outlet: RouterOutlet) {
        return (
            outlet &&
            outlet.activatedRouteData &&
            outlet.activatedRouteData['animation']
        );
    }

    public closeModal() {
        this.isModalDialogVisible = false;
    }

    ngOnDestroy(): void {
        this._destroyed$.next();
        this._destroyed$.complete();
    }
}

import { Observable, Subject, takeUntil } from 'rxjs';

import { AsyncPipe, NgClass } from '@angular/common';
import {
    ChangeDetectionStrategy,
    Component,
    ElementRef,
    EventEmitter,
    HostListener,
    Inject,
    OnDestroy,
    OnInit,
    Output,
    ViewChild,
    input,
} from '@angular/core';
import {
    FormControl,
    FormGroup,
    ReactiveFormsModule,
    Validators,
} from '@angular/forms';

import { Store } from '@ngrx/store';

import { AuthActions } from '@layout/store/auth-store/auth.actions';
import { ImagesActions } from '@layout/store/images-store/images.actions';
import { selectCloseImageUrl } from '@layout/store/images-store/images.selectors';
import { TAuthUser } from '@layout/store/model/auth-user.type';
import { TProfile } from '@layout/store/model/profile.type';

@Component({
    selector: 'cv-login-form',
    standalone: true,
    imports: [ReactiveFormsModule, NgClass, AsyncPipe],
    templateUrl: './login-form.component.html',
    styleUrls: ['./login-form.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class LoginFormComponent implements OnInit, OnDestroy {
    @ViewChild('modal', { static: false })
    public modal!: ElementRef;
    @Output() public emittedModalHide = new EventEmitter<boolean>();
    @HostListener('document:mousemove', ['$event'])
    public header = input.required<string>();
    public imageUrl$!: any;
    public url!: any;
    public authForm!: FormGroup;
    public user: TProfile | null = null;
    public whiteModeImages$!: Observable<string[]>;
    public closeImageUrl$!: Observable<string>;

    public onMouseMove(event: MouseEvent) {
        const target = event.target as HTMLElement;
        if (!this.modal.nativeElement.contains(target)) {
            this.modal.nativeElement.classList.add('dimmed');
        } else {
            this.modal.nativeElement.classList.remove('dimmed');
        }
    }

    private _destroyed$: Subject<void> = new Subject();

    constructor(@Inject(Store) private _store$: Store<TAuthUser>) {}

    ngOnInit(): void {
        const isWhiteMode = true;
        this._store$.dispatch(ImagesActions.getCloseImg({ mode: isWhiteMode }));

        this._createForm();
        this._authFormListener();
        this._store$.dispatch(ImagesActions.loadThemelessPicturesImages());
        this.closeImageUrl$ = this._store$.select(selectCloseImageUrl);
    }

    public confirmModalDialog() {
        this._checkAuth();
        this.emittedModalHide.emit(true);
    }

    public onBackgroundClick(event: MouseEvent): void {
        const target = event.target as HTMLElement;
        if (target.classList.contains(this.modal.nativeElement.classList)) {
            this.closeModalDialog();
        }
    }

    public closeModalDialog() {
        this.emittedModalHide.emit(true);
    }

    public resetModalDialog() {
        this.authForm.patchValue({
            email: '',
            password: '',
        });
    }

    private _checkAuth() {
        const { email, password, guest } = this.authForm.value;
        if (guest) {
            this._store$.dispatch(AuthActions.getLoginGuest());
        } else if (this.authForm.valid) {
            this._store$.dispatch(AuthActions.getLogin({ email, password }));
        } else {
            const error = new Error('Invalid form');
            this._store$.dispatch(AuthActions.getLoginError({ error }));
        }
    }

    private _authFormListener() {
        this.authForm
            .get('guest')
            ?.valueChanges.pipe(takeUntil(this._destroyed$))
            .subscribe((isGuest) => {
                if (isGuest) {
                    this.authForm.get('email')?.disable();
                    this.authForm.get('password')?.disable();
                } else {
                    this.authForm.get('email')?.enable();
                    this.authForm.get('password')?.enable();
                }
            });
    }

    private _createForm(): FormGroup {
        this.authForm = new FormGroup({
            email: new FormControl('', {
                validators: [
                    Validators.required,
                    Validators.email,
                    Validators.minLength(3),
                ],
            }),
            password: new FormControl('', {
                validators: [Validators.required, Validators.minLength(3)],
            }),
            guest: new FormControl(false),
        });
        return this.authForm;
    }

    ngOnDestroy(): void {
        this._destroyed$.next();
        this._destroyed$.complete();
    }
}

