                    border-radius: 10px;
                    background-color: color.$lightGrey;
                    padding: 18px 20px;
                    width: 100%;
                    font-style: 16px;
                }

                &_input:focus,
                &_input:active {
                    border: 1px solid color.$blue;
                }

                &_submit-buttons {
                    display: flex;
                    row-gap: 26px;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;

                    > * {
                        cursor: pointer;
                        border-radius: 50px;
                        width: calc(100% - 140 * (100vw / 1920));
                        height: 52px;
                        font-size: 16px;
                    }

                    .submit-button {
                        cursor: pointer;
                        background-color: color.$black;
                        color: color.$white;
                    }

                    .reset-button {
                        transition: transform 0.6s ease-in-out;
                        background-color: transparent;
                        color: color.$black;
                    }

                    .reset-button:hover {
                        color: color.$blue;
                    }

                    .disabled-submit {
                        cursor: not-allowed;
                        background-color: color.$darkGray;
                    }

                    .disabled-reset {
                        cursor: not-allowed;
                        color: color.$darkGray;
                    }
                }
            }

            .content-form_guest {
                column-gap: 12px;
                justify-content: center;
                align-items: center;
                color: color.$darkGray;
                font-size: 14px;

                .content-form_checkbox {
                    position: relative;
                    appearance: none;
                    transition:
                        background-color 0.3s,
                        border-color 0.3s;
                    cursor: pointer;
                    outline: none;
                    border: 2px solid color.$darkGray;
                    border-radius: 4px;
                    width: 24px;
                    height: 24px;

                    &:checked {
                        border-color: color.$black;
                        background-color: color.$black;
                    }

                    &:checked::after {
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        border-radius: 2px;
                        background-color: color.$white;
                        width: 12px;
                        height: 12px;
                        content: '';
                    }
                }
            }
        }
    }
}

.modal-background:hover {
    background-color: rgba(255, 255, 255, 0.1);
