@use '../../../../assets/styles/variables' as color;
@use '../../../../assets/styles/mixins/glassmorphism.mixin' as glassmorphism;

.projects {
    padding-top: 140px;
    padding-bottom: 170px;
    height: 100%;
    overflow-y: auto;
}

.grid-container {
    display: grid;
    grid-template-columns: repeat(8, 1fr);
    gap: 10px;
}

.grid-container > .project {
    position: relative;
    border-radius: 20px;
    background-color: lightblue;
    padding: 20px;
    height: 500px;
    overflow: hidden;
}

.repository-panel {
    display: flex;
    position: absolute;
    bottom: 0;
    left: 0;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-start;
    transition: height 0.4s ease-in-out;
    padding: 40px 24px;
    @include glassmorphism.projects_glassmorphism;
    border-radius: 20px;
    width: 100%;
    height: 20%;

    &__description {
        display: flex;
        row-gap: 8px;
        flex-direction: column;
    }

    &__name {
        font-weight: 700;
        font-size: 20px;
    }

    > * {
        color: color.$black;
    }
}

.repository-aside {
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    opacity: 0;
    transition: opacity 0.4s ease-in-out;
    width: 100%;
}

.project:hover .repository-panel {
    height: 75%;
}

.project:hover .repository-aside {
    opacity: 1;
}

.item2,
.item4,
.item3,
.item5 {
    grid-column: span 2;

    .repository-aside {
        row-gap: 20px;
        flex-direction: column;
        align-items: center;
    }
}

.item1,
.item6 {
    grid-column: span 4;
}

@use '../../../../../assets/styles/variables' as color;
@use '../../../../../assets/styles/mixins/glassmorphism.mixin' as glassmorphism;

.repository-panel.dark-theme {
    @include glassmorphism.projects_glassmorphism-dm;

    .repository__name {
        font-weight: 700;
        font-size: 20px;
    }

    > * {
        color: color.$white;
    }
}

